CREATE SCHEMA `mrmilho` ;
USE `mrmilho`;
CREATE TABLE `mrmilho`.`items`  (
`id` INT NOT NULL , 
`ItemName` varchar(45) NOT NULL,
`ValueNpc` varchar(45) NOT NULL,
`ValueMarket` varchar(45) DEFAULT NULL,
`url` varchar(100) DEFAULT NULL,
PRIMARY KEY (`id`));


CREATE TABLE `mrmilho`.`pokemons` ( `id` INT NOT NULL, `Name` VARCHAR(45) NOT NULL,`Droptable` INT NOT NULL,PRIMARY KEY (`id`));



CREATE TABLE `mrmilho`.`droplist`(`id` INT NOT NULL,`idpokemon` INT NOT NULL,`iditem` INT NOT NULL,PRIMARY KEY (`id`));


CREATE TABLE `mrmilho`.`accounts`(`id` INT NOT NULL AUTO_INCREMENT,`username` VARCHAR(45) NOT NULL ,`password` VARCHAR(150) NOT NULL,`idaccess` INT NOT NULL, `token` VARCHAR(45),PRIMARY KEY (`id`));

CREATE TABLE `mrmilho`.`access`(`id` INT NOT NULL ,`access` VARCHAR(45) NOT NULL ,PRIMARY KEY (`id`));

